<template>    <div class="container">        <div class="new">            <label for="Quote"><h1>Quote</h1></label>            <textarea type="text" v-model="newQuote"></textarea>            <button @click.prevent="addQuote">Add Quote</button>        </div>        <div>            <app-quote :quotes="quotes" @quoteDelete = "removeQuote"></app-quote>        </div>    </div></template><script>    import quoteGrid from '../components/quotes/quoteGrid.vue'    export default {        data () {            return {                maxCounts: 10,                quotes: [                    'An array of Quotes',                    'Make we check something'                ],                newQuote: ''            }        },        components: {            appQuote: quoteGrid        },        methods: {            addQuote () {                this.quotes.push(this.newQuote)                return this.newQuote = ''            },            removeQuote (index) {                return this.quotes.splice(index, 1)            }        }    }</script><style scoped>    .container {        min-height: 80vh;    }    .new {        margin: 50px 150px 30px 150px;        /* display: grid;        grid-template: 50px 150px 50px / 1fr;        grid-gap: 30px;        justify-content: center; */    }    .new textarea {        height: 20vh;          width: 100%;         font-size: 30px;    }        .new button {        height: 40px;        width: 100px;        border: none;        background-color: #00f;        /* margin: 10px 0; */        color: whitesmoke;    }</style>